@use "var";

@mixin main-appearance($background){
    background: $background;
    border: solid 1px black;
    border-radius: 2px;
}

@mixin big-title{
    margin: 1rem 2rem;
    font-size: 2rem;
    font-weight: 800;
    color: var.$main-blue;
}

