@use "../var";

.navigation{
    position: absolute;
    right: 2rem;
    bottom: 0;
    display: flex;
    align-items: center;
    gap: 1rem;
    background: #e6e6e6;
    height: 2rem;
    padding: 0 0.5rem;
    z-index: 5;
    border: solid 1px black;
    border-bottom: none;

    a{
        color: white;
        padding: 0.2rem 1rem;
        // background: #359bde;
        border-radius: 5px 5px 0 0;
        height: 100%;
        position: relative;
        line-height: 1.4rem;
    }
    &__notification{
        width: 2.5rem;
        height: 100%;
        background-color: #020202;
        font-size: 1em;
        display: inline-block;
        text-align: center;
        border-radius: 20px;
        /* border: solid 1px red; */
        margin: -1rem 0;
        position: relative;
        right: 0.3rem;
        box-shadow: inset 0 1px #7eccffa6;
        }
    
    &__icon{
        max-height: 1rem;
        position: absolute;
        left: 0.2rem;
    }

    &__account {
        display: flex;
        align-items: center;
        justify-content: space-between;
        padding: 0.2rem;
        min-width: 10em;
        &--name{
            width: 100%;
            padding: 0 0.5rem;
        }
        &--pic{
            height: 2rem
        }
    }
}

