@use "../var";
@use "../mixin";

.side{
    padding: var.$default-padding;
    min-width: 15rem;
    margin: 0 0.5em;
    // @include mixin.main-appearance(var.$content-background);
    &__title {
        font-size: 1.4rem;
    }
    &__current{
        margin: 0.3rem;
        color: var.$main-blue;
        font-weight: 700;
    }
    &__container {
        margin-bottom: 2rem;
    }

    &__soarer{
        background: var.$content-background-2;
        border: solid 1px var.$light-grey;
        margin: 0.3rem 0;
        gap: 0.2rem;
        display: grid;
        grid-template-rows: auto;
        grid-template-columns: 3rem 4.5rem auto;
        grid-template-areas: 
        "img profile profile"
        "img follow ."
        ;
        padding: 0.3rem;
        img{
            max-width: 3rem;
            grid-area: img;
        }
        p{
            grid-area: profile;
        }
    }
    &__follow{
        grid-area: follow;
        &:hover{
            text-decoration: none !important;
        }
    }
    &__user{
        background: var.$content-background-2;
        border-radius: 5px;
        overflow: hidden;
        border: solid 1px var.$light-grey;
        &--profile{
            height: 7rem;
            position: relative;
            img{
                max-height: 4rem;
                vertical-align: middle;
                position: absolute;
                left: 0.5rem;
                bottom: 0;
                box-shadow: 0 0 3px 1px #020202a1;
            }
            p{
                display: inline;
                vertical-align: middle;
                position: absolute;
                left: 5rem;
                bottom: 1rem;
            }
        }
        &--stats{
            padding: 0.5rem;
            display: flex;
            flex-direction: column;
            gap: 0.5rem;
            p{
                display: inline-block;
            }
            span{
                color: var.$main-blue;
            }
        }
        &--banner{
            width: 100%;
            height: 4rem;
            background-size: cover;
            background-position: 0px -13px;
            background-image: url(/img/banner.webp);
        }
    }
}

sources.styles\\components\\_main-content.scss:
@use "../var";
@use "../mixin";

.main-content{
    min-width: 15rem;
    // @include mixin.main-appearance(var.$content-background);
    border-right: solid 1px var.$light-grey;

    &__title{
        @include mixin.big-title;
    }

    &__bigname {
        font-size: 3rem;
        position: absolute;
        left: -8.9rem;
        top: 11.5rem;
        /* writing-mode: vertical-lr; */
        opacity: 0.1;
        transform: rotate(270deg);
    }

    &__see-more {
        padding: 1rem;
        text-align: center;
        &--button{
            position: relative;
            color: var.$main-blue;
            font-size: 1.7rem;
            font-weight: 700;
            padding: 0.5rem;
            border: 1px solid black;
            border-radius: 5px;
            width: 100%;
            height: 8rem;
            display: flex;
            align-items: center;
            margin: auto;
            text-align: left;
            overflow: hidden;
            &:hover{
                text-decoration: none;
                span{
                    left: 3em;
                    transform: scale(1.5);
                    transition: all 0.2s ease-out;
                }
                img{
                    transition: all 0.4s ease-out;
                    max-width: 8em;
                    right: -0.7em;
                    top: -3.6em;
                    opacity: 1;
                }
            }
            span{
                position: relative;
                z-index: 1;
                left: 0em;
                transition: all 0.2s ease-in;
            }
            img{
                transition: all 0.2s ease-in;
                max-width: 6em;
                user-select: none;
                position: absolute;
                z-index: 0;
                right: 7em;
                top: -0.6em;
                opacity: 0;
            }
        }
    }
}

